INSERT INTO tb_news VALUES("164","22","48","0","0","0","Mừng sinh nhật đặc biệt với DJ Jennifer Lopez",NULL,NULL,"Nữ nghệ sĩ mang hai d&ograve;ng m&aacute;u T&acirc;y Ban Nha v&agrave; Thụy Điển dừng ch&acirc;n tại Nutz fun pub (kh&aacute;ch sạn Sheraton, H&agrave; Nội) v&agrave;o cuối th&aacute;ng n&agrave;y, để khuấy động bữa tiệc &ldquo;bốn năm mới c&oacute; một lần&rdquo; d&agrave;nh cho những người sinh ng&agrave;y 29/2.",NULL,NULL,"<p class=\"Lead\"><strong>Nữ nghệ sĩ mang hai d&ograve;ng m&aacute;u T&acirc;y Ban Nha v&agrave; Thụy Điển dừng ch&acirc;n tại Nutz fun pub (kh&aacute;ch sạn Sheraton, H&agrave; Nội) v&agrave;o cuối th&aacute;ng n&agrave;y, để khuấy động bữa tiệc &ldquo;bốn năm mới c&oacute; một lần&rdquo; d&agrave;nh cho những người sinh ng&agrave;y 29/2.</strong> </p>
<table cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\" border=\"0\">
    <tbody>
        <tr>
            <td><img height=\"364\" src=\"http://vnexpress.net/Vietnam/Van-hoa/Am-nhac/2008/02/3B9FFB12/Jen-DJ-28.jpg\" width=\"250\" border=\"1\" alt=\"\" /></td>
        </tr>
        <tr>
            <td class=\"Image\">DJ Jennifer Lopez. Ảnh: <em>Evolutionmedia</em>.</td>
        </tr>
    </tbody>
</table>
<p class=\"normal\">Tr&ugrave;ng t&ecirc;n với nữ ca sĩ lừng danh thế giới, Jennifer Lopez được gọi l&agrave; &quot;Thi&ecirc;n thần t&oacute;c v&agrave;ng&quot;, một nữ nghệ sĩ biết c&aacute;ch truyền sức sống mới mẻ v&agrave; đầy đam m&ecirc; cho kh&aacute;n giả, với phong c&aacute;ch mix nhạc trẻ trung, phối hợp h&agrave;i h&ograve;a giữa truyền thống v&agrave; hiện đại.</p>
<p class=\"normal\">Jennifer trở th&agrave;nh DJ chuy&ecirc;n nghiệp từ năm 2003, sau 5 năm l&agrave;m người mẫu. C&ocirc; từng biểu diễn tại những c&acirc;u lạc bộ nổi tiếng nhất Thụy Điển như Spy Bar, K&ouml;ket Club (Stockholm), l&agrave; gương mặt quen thuộc trong c&aacute;c chương tr&igrave;nh biểu diễn thời trang v&agrave; truyền h&igrave;nh của v&ugrave;ng Scandinavia. Năm 2008, c&ocirc; đua t&agrave;i c&ugrave;ng c&aacute;c đồng nghiệp nữ trong chuyến lưu diễn quốc tế mang t&ecirc;n&nbsp;<i>God is a djane - Ch&uacute;a l&agrave; nữ DJ</i>. </p>
<p class=\"normal\">Buổi biểu diễn duy nhất của Jennifer Lopez tại Việt Nam bắt đầu từ 20h ng&agrave;y 29/2. Đ&acirc;y l&agrave; một chặng trong chuyến lưu diễn Abu Dhabi (C&aacute;c tiểu vương quốc Ảrập thống nhất), Ai Cập, Nauy v&agrave; Đức của người đẹp n&agrave;y.</p>",NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,"164.jpg",NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,"28/02/08","11:29 am","1","1","0","0","1",NULL,"106","0");
