INSERT INTO tb_news VALUES("195","3","31","0","0","0","Nghỉ hè cùng BMW serie 3",NULL,NULL,"<p class=\"Lead\">Sở hữu xe BMW serie 3 kh&aacute;ch h&agrave;ng sẽ được nghỉ miễn ph&iacute; tại Sheraton Nha Trang Hotel &amp; Spa v&agrave; được đưa đ&oacute;n bằng xe BMW.</p>",NULL,NULL,"<p>
<table cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\" border=\"0\">
    <tbody>
        <tr>
            <td><img height=\"300\" alt=\"\" src=\"http://www.vnexpress.net/Files/Subject/3B/A1/CD/C1/bm-2.jpg\" width=\"450\" border=\"1\" /></td>
        </tr>
    </tbody>
</table>
</p>
<p class=\"Normal\">Từ ng&agrave;y 11/6 đến 31/7, khi mua xe BMW serie 3 (320i Executive, 320i v&agrave; 325i Lifestye) tại c&aacute;c phong trưng b&agrave;y của BMW Euro Auto, kh&aacute;ch h&agrave;ng sẽ nhận được g&oacute;i ưu đ&atilde;i 3 đ&ecirc;m nghỉ miễn ph&iacute; d&agrave;nh cho 2 người tại kh&aacute;ch sạn Sheraton Nha Trang Hotel &amp; Spa v&agrave; được đưa đ&oacute;n từ s&acirc;n bay đến kh&aacute;ch sạn (ngược lại). </p>
<p>
<table cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\" border=\"0\">
    <tbody>
        <tr>
            <td><img height=\"283\" alt=\"\" src=\"http://www.vnexpress.net/Files/Subject/3B/A1/CD/C1/bm1.jpg\" width=\"450\" border=\"1\" /></td>
        </tr>
    </tbody>
</table>
</p>
<p class=\"Normal\">Đặc biệt, 10 kh&aacute;ch h&agrave;ng mua xe đầu ti&ecirc;n trong chương tr&igrave;nh n&agrave;y sẽ nhận được quả b&oacute;ng Jabulani World Cup 2010 ch&iacute;nh h&atilde;ng c&oacute; chữ k&yacute; của nh&acirc;n vật nổi tiếng trong l&agrave;ng b&oacute;ng đ&aacute; Việt Nam. </p>
<p class=\"Normal\">BMW serie 3 phi&ecirc;n bản Lifestyle trang bị hệ thống định vị to&agrave;n cầu ch&iacute;nh h&atilde;ng t&iacute;ch hợp bản đồ Việt Nam, hệ thống giải tr&iacute; d&agrave;nh cho ghế sau v&agrave; m&acirc;m đ&uacute;c kiểu thể thao hiện đại.</p>
<p class=\"Normal\">Tham khảo trang web <a class=\"Normal\" onclick=\"return openMeExt(this,1,1,1,1,1,0,1,1,300,400,0,0,0)\" href=\"http://www.bmw.vn/\">http://www.bmw.vn/</a>. </p>
<p class=\"Normal\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (Nguồn: <em>BMW)</em></p>",NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,"195.jpg",NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,"12/06/10","1:20 am","1","1","1","0","1",NULL,"14","0");
