INSERT INTO tb_news VALUES("249","3","86","0","0","0","Người đẹp BMW Z4 2010 ở Sài Gòn",NULL,NULL,"Thiết kế mới với nhiều kh&aacute;c biệt so với thế hệ trước, BMW Z4 2010 sở hữu những đường n&eacute;t thể thao hơn. Xe nổi bật b&ecirc;n người mẫu dưới nắng S&agrave;i G&ograve;n.",NULL,NULL,"<table cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\" border=\"0\">
