INSERT INTO tb_news VALUES("281","3","31","0","0","0","Hé lộ hình ảnh Beetle thế hệ mới",NULL,NULL,"Hồi đầu năm nay đ&atilde; xuất hiện th&ocirc;ng tin Volkswagen chuẩn bị ra mắt mẫu  New Beetle thế hệ mới, v&agrave; giờ đ&acirc;y c&aacute;nh săn ảnh đ&atilde; &ldquo;t&oacute;m gọn&rdquo; chiếc xe  tr&ecirc;n đường chạy thử.",NULL,NULL,"<div align=\"center\" style=\"margin: 0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"\">
<div><img width=\"450\" align=\"center\" src=\"http://dantri.vcmedia.vn/Uploaded/2010/10/05/Beetle-New-051010-3.jpg\" style=\"margin: 5px;\" alt=\"\" /></div>
</span></div>
<div style=\"margin: 0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"\"></span>&nbsp;</div>
<div style=\"margin: 0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"\">Hiện  chưa c&oacute; th&ocirc;ng tin về thời điểm xe New Beetle thế hệ mới sẽ ch&iacute;nh thức ra  mắt, c&oacute; tin đồn l&agrave; ngay trong th&aacute;ng tới, tại Triển l&atilde;m &ocirc; t&ocirc; Los Angeles  2010, nhưng cũng c&oacute; th&ocirc;ng tin l&agrave; phải tới Triển l&atilde;m &ocirc; t&ocirc; Detroit hoặc  thậm ch&iacute; l&agrave; Geneva năm 2011.</span></div>
<p style=\"margin: 0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"\">&nbsp;</span></p>
<p style=\"margin: 0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"\">D&ugrave;  chiếc xe vẫn đang trong qu&aacute; tr&igrave;nh ho&agrave;n thiện, nhưng những h&igrave;nh ảnh chụp  trộm tr&ecirc;n đường thử cho thấy New Beetle thế hệ mới mang nhiều n&eacute;t thiết  kế của xe Volkswagen Ragster concept tr&igrave;nh l&agrave;ng lần đầu v&agrave;o năm 2005.</span></p>
<p style=\"margin: 0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"\">&nbsp;</span></p>
<p style=\"margin: 0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"\">Theo  đ&oacute;, th&acirc;n xe d&agrave;i hơn, dẹt hơn v&agrave; thiết kế tổng thể xe giảm n&eacute;t hiền l&agrave;nh,  dễ thương, thay v&agrave;o đ&oacute; l&agrave; phong c&aacute;ch nam t&iacute;nh hơn. C&oacute; lẽ Volkswagen  đang cố gắng xo&aacute; bỏ h&igrave;nh mẫu xinh xinh d&agrave;nh cho nữ giới l&acirc;u nay gắn với  Beetle.</span></p>
<p style=\"margin: 0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"\">&nbsp;</span></p>
<div style=\"margin: 0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"\">Xe Beetle 2012 sẽ d&ugrave;ng chung cơ sở gầm bệ với VW Golf VI phi&ecirc;n bản mới, v&agrave; c&oacute; thể hệ thống động cơ cũng vậy.</span></div>
<div style=\"margin: 0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"\"></span>&nbsp;</div>
<div align=\"center\" style=\"margin: 0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"\"><img src=\"http://dantri.vcmedia.vn/Uploaded/2010/10/05/Beetle-New-051010-1.jpg\" alt=\"\" /></span></div>
<span style=\"\">
<div align=\"center\" style=\"margin: 0cm 0cm 0pt;\" class=\"MsoNormal\"><br /><img src=\"http://dantri.vcmedia.vn/Uploaded/2010/10/05/Beetle-New-051010-5.jpg\" alt=\"\" /></div>
<div align=\"center\" style=\"margin: 0cm 0cm 0pt;\" class=\"MsoNormal\"><br /><img src=\"http://dantri.vcmedia.vn/Uploaded/2010/10/05/Beetle-New-051010-2.jpg\" alt=\"\" /><br /><br /><img src=\"http://dantri.vcmedia.vn/Uploaded/2010/10/05/Beetle-New-051010-4.jpg\" alt=\"\" /><br /></div>
</span>
<p style=\"margin: 0cm 0cm 0pt;\" class=\"MsoNormal\"><span style=\"\"></span></p>
<span style=\"font-weight: bold;\"><br /></span>
<div style=\"text-align: right;\"><span style=\"font-weight: bold;\">Nhật Minh</span><br /><span style=\"font-weight: bold;\"></span></div>
