INSERT INTO tb_news VALUES("283","3","86","0","0","0","Hồng Ánh dịu dàng bên \'xế cưng\'",NULL,NULL,"Hồng &Aacute;nh k&eacute;o d&agrave;i th&ecirc;m danh s&aacute;ch th&agrave;nh vi&ecirc;n Mercedes  Celebrity Club khi  lựa chọn chiếc sedan cao cấp nhất lắp r&aacute;p tại Việt  Nam E300 với những  trang bị như điều h&ograve;a tự động 4 v&ugrave;ng độc lập",NULL,NULL,"nhớ vị  tr&iacute; ghế, cửa sổ trời to&agrave;n cảnh panorama, gương chiếu hậu chống ch&oacute;i hay  gạt mưa tự động.
