INSERT INTO tb_news VALUES("294","3","86","0","0","0","Hoa hậu xế độ chụp ảnh lịch 2011",NULL,NULL,"Kristin Zippel, người đẹp tới từ Mering (bang Bavaria, Đức) l&agrave; nh&acirc;n vật ch&iacute;nh trong bộ lịch năm mới d&agrave;nh cho d&acirc;n m&ecirc; xe hơi.",NULL,NULL,"<p class=\"Normal\">Những h&igrave;nh ảnh khoe th&acirc;n h&igrave;nh n&oacute;ng bỏng của Zippel được thực hiện tại Detroit v&agrave; Sandusky (Mỹ).</p>
