INSERT INTO tb_news VALUES("302","3","31","0","0","0","Hyundai Thành Công lắp Avante tại Việt Nam",NULL,NULL,"Mẫu sedan hạng trung c&oacute; hai phi&ecirc;n bản động cơ l&agrave; 1.6 v&agrave; 2.0 với mức gi&aacute; dao động trong khoảng 30.000 USD.",NULL,NULL,"<p class=\"Normal\">Nh&agrave; ph&acirc;n phối c&aacute;c sản phẩm Hyundai tại Việt Nam lần  đầu tiết lộ về sản phẩm sẽ lắp r&aacute;p trong nước. D&ugrave; sản xuất ngo&agrave;i H&agrave;n  Quốc nhưng chiếc sedan hạng trung n&agrave;y c&oacute; thể mang t&ecirc;n Avante, thay v&igrave;  Elantra như th&ocirc;ng lệ.</p>
<table width=\"1\" cellspacing=\"0\" cellpadding=\"3\" border=\"0\" align=\"center\">
    <tbody>
        <tr>
            <td class=\"Image\"><img width=\"450\" height=\"300\" border=\"1\" alt=\"Elantra sẽ có tên Avante tại Việt Nam khi Hyundai Thành Công lắp ráp.\" src=\"http://vnexpress.net/Files/Subject/3B/A2/1A/E0/Avante.jpg\" /></td>
        </tr>
        <tr>
            <td class=\"Image\">Elantra sẽ c&oacute; t&ecirc;n Avante tại Việt Nam khi Hyundai Th&agrave;nh C&ocirc;ng lắp r&aacute;p.</td>
        </tr>
    </tbody>
</table>
<p class=\"Normal\">Điều kh&aacute;c biệt l&agrave; Avante do Hyundai Th&agrave;nh C&ocirc;ng lắp r&aacute;p  kh&ocirc;ng phải thế hệ mới đang được b&aacute;n ở H&agrave;n Quốc. Ngoại thất mẫu xe n&agrave;y  giống như chiếc Elantra Hybrid với d&agrave;n đền LED gắn ở đ&egrave;n pha.</p>
<p class=\"Normal\">Chưa c&oacute; thời điểm xe tr&igrave;nh l&agrave;ng nhưng mức gi&aacute; được tiết lộ nằm trong khoảng 30.000 USD.</p>
<p class=\"Normal\">Mẫu sedan hạng trung sẽ được trưng b&agrave;y tại gian h&agrave;ng  của Hyundai Th&agrave;nh C&ocirc;ng tại triển l&atilde;m AutoExpo từ 21/10 đ&ecirc;n 24/10 tại  Trung t&acirc;m hội chợ triển l&atilde;m quốc tế T&acirc;n B&igrave;nh, TP HCM.</p>
<p class=\"Normal\" style=\"text-align: right;\">theo: <span style=\"font-style: italic; font-weight: bold;\">Vnexpress</span><br /></p>",NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,"302.jpg",NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,"16/10/10","1:48 pm","1","1","1","0","2",NULL,"185","0");
