INSERT INTO tb_news VALUES("307","3","31","0","0","0","Mercedes trình làng hai mẫu đặc biệt",NULL,NULL,"Concept 358 l&agrave; chiếc C63 AMG duy nhất tr&ecirc;n thế giới sơn m&agrave;u cam ngọc  trai, đang được trưng b&agrave;y tại Sydney Motor Show c&ugrave;ng với phi&ecirc;n bản đặc  biệt SLS AMG Night Black.",NULL,NULL,"<p class=\"Normal\">Cả hai mẫu tr&ecirc;n đều do Mercedes Australia thiết kế nhằm đề cao thị trường nội địa v&agrave; thể hiện những &yacute; tưởng thiết kế mới.</p>
<table width=\"1\" cellspacing=\"0\" cellpadding=\"3\" border=\"0\" align=\"center\">
